import React, { useState } from 'react'
import axios from 'axios';

const ButtonText = () => {
  const [statusGet, setStatusGet] = useState(false);
  const [loadingGet, setLoadingGet] = useState(null);

  const handlerClick = () => {
    setLoadingGet(true);
    setStatusGet(false);
    setTimeout()
    axios
      .get('') // url
      .then((response) => {
        console.log(response.data);
        setStatusGet(true);
      })
      .catch((error) => {
        console.log(error);
        setStatusGet(false);
      })
      .finally(() => {
        setLoadingGet(false);
      });
  };

  return (
    <div>
      <button
        onClick={handlerClick}
        disabled={loadingGet}
        style={statusGet ? { backgroundColor: '#a1ffa4' } : {}}
      >
        {loadingGet ? 'Загрузка...' : statusGet ? 'Данные получены' : 'Получить данные'}
      </button>
      {/* <button onClick={handlerClick}>Отправить данные</button>
      <button onClick={handlerClick}>Удалить данные</button>
      <button onClick={handlerClick}>Изменить данные</button> */}
    </div>
  )
}

export default ButtonText
