import React from 'react';

import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import useScrollTrigger from '@material-ui/core/useScrollTrigger';
import Slide from '@material-ui/core/Slide';
import { Link, Typography } from '@material-ui/core';

import LogoSvg from '../../assets/img/Logo.svg';
import LoginSvg from '../../assets/img/login-24px.svg';
import SearchSvg from '../../assets/img/search-24px.svg';

const useStyles = makeStyles({
  header: {
    backgroundColor: '#EE6002',
    height: 96,
  },
  toolbar: {
    paddingLeft: 32,
    paddingRight: 32,
    height: '100%'
  },
  logo: {
    padding: 0,
    margin: 0,
    width: 101,
    height: 32,
    '& img': {
      width: '100%',
      height: '100%'
    }
  },
  login
});

function HideOnScroll(props) {
  const { children, window } = props;
  const trigger = useScrollTrigger({ target: window ? window() : undefined });

  return (
    <Slide appear={false} direction="down" in={!trigger}>
      {children}
    </Slide>
  );
}

const Header = (props) => {
  const classes = useStyles();

  return (
    <React.Fragment>
      <HideOnScroll {...props}>
        <AppBar className={classes.header}>
          <Toolbar className={classes.toolbar}>
            <Link className={classes.logo} href='/'>
              <img src={LogoSvg} alt="logo icon" />
            </Link>
            <Link className={classes.login}>
              Войти
              <img src={LoginSvg} alt="LoginSvg icon" />
            </Link>
            <Link className={classes.search}>
              <img src={SearchSvg} alt="LoginSvg icon" />
            </Link>
          </Toolbar>
        </AppBar>
      </HideOnScroll>
    </React.Fragment>
  )
}

export default Header;
