import React, { useState } from 'react'
import axios from 'axios';

const ButtonText = () => {
  const [statusGet, setStatusGet] = useState(false);
  const [loaderGet, setLoaderGet] = useState()

  const handlerClick = () => {
    axios
      .get('http://localhost:3001/')
      .then((response) => {
        console.log(response.data);
        setStatusGet(true);
      })
      .catch((error) => {
        console.log(error);
        setStatusGet(false);
      })
  };

  return (
    <div>
      <button onClick={handlerClick}>{statusGet ? 'Done' : 'Получить данные'}</button>
      <button onClick={handlerClick}>Отправить данные</button>
      <button onClick={handlerClick}>Удалить данные</button>
      <button onClick={handlerClick}>Изменить данные</button>
    </div>
  )
}

export default ButtonText
