import React from 'react';

import useScrollTrigger from '@material-ui/core/useScrollTrigger';
import AppBar from '@material-ui/core/AppBar';
import Slide from '@material-ui/core/Slide';
import Button from '../Button';
// import { SvgIcon } from '@material-ui/core';

import './Header.scss'

import LogoSvg from '../../assets/img/Logo.svg';
// import LoginSvg from '../../assets/img/login-24px.svg';


function HideOnScroll(props) {
  const { children, window } = props;
  const trigger = useScrollTrigger({ target: window ? window() : undefined });

  return (
    <Slide appear={false} direction="down" in={!trigger}>
      {children}
    </Slide>
  );
}

const Header = (props) => {
  return (
    <React.Fragment>
      <HideOnScroll {...props}>
        <AppBar>
          <div className='header bgc--orange'>
            <a href="/">
              <img src={LogoSvg} alt="logo" />
            </a>
            <Button
              text={'Войти'}
              href='/login'
              iconSvgPath='M11 17L9.6 15.6L12.2 13H2L2 11L12.2 11L9.6 8.4L11 
              7L16 12L11 17ZM20 5H12V3L20 3C21.1 3 22 3.9 22 5L22 19C22 20.1
               21.1 21 20 21L12 21V19L20 19L20 5Z'
            />
            <Button
              href='/search'
              iconSvgPath='M20.6667 18.6667H19.6133L19.24 18.3067C20.5467 16.7867 21.3333 14.8133 21.3333 12.6667C21.3333 7.88 17.4533 4 12.6667 4C7.88 4 4 7.88 4 12.6667C4 17.4533 7.88 21.3333 12.6667 21.3333C14.8133 21.3333 16.7867 20.5467 18.3067 19.24L18.6667 19.6133V20.6667L25.3333 27.32L27.32 25.3333L20.6667 18.6667ZM12.6667 18.6667C9.34667 18.6667 6.66667 15.9867 6.66667 12.6667C6.66667 9.34667 9.34667 6.66667 12.6667 6.66667C15.9867 6.66667 18.6667 9.34667 18.6667 12.6667C18.6667 15.9867 15.9867 18.6667 12.6667 18.6667Z'
            />
          </div>
        </AppBar>
      </HideOnScroll>
    </React.Fragment>
  );
};

export default Header;
